import React, {Component, Fragment} from 'react'
import '../assets/css/templatemo-stand-blog.css';
import '../assets/css/custom.css';
import '../assets/css/bootstrap.min.css';
import { Row, Col} from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faShareAlt, faTags, faAngleDoubleRight } from '@fortawesome/free-solid-svg-icons'
import AppUrl from '../restApi/AppUrl';
import { GetRequest } from '../restApi/RestClient';

class Blogs extends Component{

    constructor(){
        super();
        this.state={
            postArray:[]
        }
    }

    componentDidMount(){
        GetRequest(AppUrl.AllPost).then(result=>{
            this.setState({
                postArray:result
            })
        }).catch(error=>{

        });
    }

    render(){

        const listData = this.state.postArray;
        const blogHtml = listData.map(myList=>{
            return <Col lg={12} md={12} sm={12}>
            <div className="blog-post">
                <div className="blog-thumb">
                <img src="https://templatemo.com/templates/templatemo_551_stand_blog/assets/images/blog-post-01.jpg" alt=""/>
                </div>
                <div className="down-content">
                <span>Lifestyle</span>
                <a href="post-details.html"><h4>Best Template Website for HTML CSS</h4></a>
                <ul className="post-info">
                    <li><a href="#">Admin</a></li>
                    <li><a href="#">May 31, 2020</a></li>
                    <li><a href="#">12 Comments</a></li>
                </ul>
                <p>Stand Blog is a free HTML CSS template for your CMS theme. You can easily adapt or customize it for any kind of CMS or website builder. You are allowed to use it for your business. You are NOT allowed to re-distribute the template ZIP file on any template collection site for the download purpose. <a rel="nofollow" href="https://templatemo.com/contact" target="_parent">Contact TemplateMo</a> for more info. Thank you.</p>
                <div className="post-options">
                    <Row>
                    <Col lg={6} md={6} sm={6}>
                        <ul className="post-tags">
                        <li><FontAwesomeIcon icon={faTags} className="icon-style" /></li>
                        <li><a href="#">Beauty</a>,</li>
                        <li><a href="#">Nature</a></li>
                        </ul>
                    </Col>
                    <Col lg={6} md={6} sm={6}>
                        <ul className="post-share">
                        <li><FontAwesomeIcon icon={faShareAlt} className="icon-style" /></li>
                        <li><a href="#">Facebook</a>,</li>
                        <li><a href="#"> Twitter</a></li>
                        </ul>
                    </Col>
                    </Row>
                </div>
                </div>
            </div>
            </Col>

        })



        return(
            <Fragment>

                <div className="all-blog-posts">
                <Row>

                    {this.blogHtml}

                    <Col lg={12} md={12} sm={12}>
                    <ul className="page-numbers">
                        <li><a href="#">1</a></li>
                        <li class="active"><a href="#">2</a></li>
                        <li><a href="#">3</a></li>
                        <li><a href="#"><i class="fa fa-angle-double-right"></i> <FontAwesomeIcon icon={faAngleDoubleRight} className="icon-style"  /> </a></li>
                    </ul>
                    </Col>
                </Row>
                </div>
                    
            </Fragment>
        )
    }
}

export default Blogs;
