import React, {Component, Fragment} from 'react'
import '../assets/css/templatemo-stand-blog.css';
import '../assets/css/custom.css';
import '../assets/css/bootstrap.min.css';
import { Col} from 'react-bootstrap';
import {Link} from 'react-router-dom';
import axios from 'axios';
import ApiUrl from '../../^untitled:Untitled-2';

class Category extends Component{

    constructor(){
      super();
      this.state={
        categoryArray:[]
      }
    }

    componentDidMount(){
      axios.get(ApiUrl.Categories).then(response=>{
        this.setState({
          categoryArray:response.data
        })
      }).catch(error=>{
        this.setState({
          categoryArray:[]
        })
      });
    }

    render(){

      const listData = this.categoryArray;
      const myView = listData.map(myList=>{
        return <li><Nav to="/about">- {myList.name}</Nav></li>
      })

        return(
            <Fragment>
                <Col lg={12} md={12} sm={12}>
                  <div class="sidebar-item categories">
                    <div class="sidebar-heading">
                      <h2>Categories</h2>
                    </div>
                    <div class="content">
                      <ul>
                        {myView}
                      </ul>
                    </div>
                  </div>
                </Col>
            </Fragment>
        )
    }
}

export default Category;
