import React, {Component, Fragment} from 'react'
import '../assets/css/templatemo-stand-blog.css';
import '../assets/css/custom.css';
import '../assets/css/bootstrap.min.css';
import {Container, Row, Col} from 'react-bootstrap';

import Header from "../components/Header";
import Category from "../components/Category";
import Recentposts from "../components/Recentposts";
import Search from "../components/Search";
import Tags from "../components/Tags";
import PostDetails from '../components/PostDetails';
import Footer from '../components/Footer';
import axios from 'axios';
import ApiUrl from '../api/ApiUrl';

class Single extends Component{

    constructor({match}){
      super();
      this.state={
        slugUrl:match.params.slug,
        title:'...',
        description:'...',
        image:'',
        tags:'',
        category:'',
        author_name:'',
        comments:'',
        publishDate:'',
      }

    }

    componentDidMount(){
      window.scroll(0,0);
      const slugUrl = this.props.match.params.slug;
      alert(this.state)
      axios.get(ApiUrl.singlePost).then(response=>{
        this.setState({
          title:response[0]['title'],
          description:response[0]['description'],
          image:response[0]['image'],
          tags:response[0]['tags'],
          category:response[0]['cat_name'],
          author_name:response[0]['aut_name'],
          comments:response[0]['comments'],
          publishDate:response[0]['publish_date'],
        })
      }).catch({
        title:'...',
        description:'...',
        image:'',
        tags:'',
        category:'',
        author_name:'',
        comments:'',
        publishDate:'',
      });
    }

    render(){
        return(
            <Fragment>

                <Header/>
                <section className="blog-posts">
                  <Container>
                    <Row>
                      <Col lg={8} md={8} sm={12}>
                        <PostDetails/>
                      </Col>
                      <Col lg={4} md={4} sm={12}>
                        <div class="sidebar">
                          <Row>
                            <Search/>
                            <Recentposts/>
                            <Category/>
                            <Tags/>
                          </Row>
                        </div>
                      </Col>
                    </Row>
                  </Container>
                </section>
                <Footer/>
                
            </Fragment>
        )
    }
}

export default Single;
