import React from 'react';
import axios from 'axios';

class appFunctions{

        static getRequest=(appURL)=>{
            return axios.get(appURL).then(response=>{
                return response.data;
            }).catch(error=>{
                return null;
            });
        }
    

}
export default appFunctions;
