import React, {Component, Fragment} from 'react';
import {Container, Row, Col} from 'react-bootstrap';
import axios from 'axios';
import ApiUrl from '../api/ApiUrl';
import { EMDate,EMExplode,EMStrToSlug } from '../helper/Helper';
import {Link} from 'react-router-dom';


class AddPost extends Component{


    constructor(){
        super();
        this.state={
            title:'',
            errorTitle:'',
            category:'',
            errorCategory:'',
            tags:'',
            errorTags:'',
            description:'',
            errorDescription:'',
            categories:[]
        }
    }
    
    componentDidMount(){
        axios.get(ApiUrl.Categories).then(response=>{
          this.setState({
            categories:response.data
          })
        }).catch(error=>{
          this.setState({
            categories:[]
          })
        });
      }
  


    render(){

        const listData = this.state.categories;
        const myView = listData.map(myList=>{
          return <option value={myList.id} >{myList.name}</option>
        })

        return(
            <Fragment>

                <section className="contact-us">
                    <Container>
                        <Row>
                        <Col lg={12} md={12} sm={12}>
                            <div className="down-contact">
                            <Row>
                                <Col lg={12} md={12} sm={12}>
                                <div class="sidebar-item contact-form">
                                    <div class="sidebar-heading">
                                    <h2>Add New Post | <span className="pull-right"><Link to="/profile">Back</Link></span></h2>
                                    </div>
                                    <div class="content">
                                    <form  noValidate onSubmit={this.onSubmit}>
                                        <Row>
                                        <Col lg={12} md={12} sm={12}>
                                            <fieldset>
                                            <input name="title" type="text" id="title" placeholder="Title" required="" />
                                            </fieldset>
                                        </Col>
                                        <Col lg={6} md={6} sm={12}>
                                            <fieldset>
                                            <select name="cat_id" required="" >
                                                <option value="" selected>Select Category</option>
                                                {myView}
                                            </select>
                                            </fieldset>
                                        </Col>
                                        <Col lg={6} md={6} sm={12}>
                                            <fieldset>
                                            <input name="tags" type="text" id="tags" placeholder="Seperate tag by (,) | Ex: Database,React,Portfolio" required="" />
                                            </fieldset>
                                        </Col>
                                        <Col lg={12} md={12} sm={12}>
                                            <fieldset>
                                            <textarea name="description" rows="6" id="description" placeholder="Description" required=""></textarea>
                                            </fieldset>
                                        </Col>
                                        <Col lg={12} md={12} sm={12}>
                                            <fieldset>
                                            <button className="main-button" type="submit" >Submit</button>
                                            </fieldset>
                                        </Col>
                                        </Row>
                                    </form>
                                    </div>
                                </div>
                                </Col>
                            </Row>
                            </div>
                        </Col>
                        
                        </Row>
                    </Container>
                </section>

 
            </Fragment>
        )
    }
}

export default AddPost;
