class ApiUrl{

    static baseUrl = "http://localhost:3000";
    static categories = this.baseUrl+"/api/categories";
    static catPost = this.baseUrl+"/api/category-post/technology";
    static allPost = this.baseUrl+"/api/posts";
    static allTags = this.baseUrl+"/api/tags";
    static singlePost = this.baseUrl+"/api/posts/guide-to-design-database";
    static recentPost = this.baseUrl+"/api/posts-recent";
    static relatedPost = this.baseUrl+"/api/posts-related/1/1";
    static search = this.baseUrl+"/api/posts-search/magna";
    static tagPost = this.baseUrl+"/api/post-tag-search/design";


}
export default ApiUrl;
