import React, { Component } from 'react'
import { login } from '../helper/UserFunctions'

class Login extends Component {
    
    constructor() {
        super()
        this.state = {
            email: '',
            emailEmpty: '',
            password: '',
            passwordEmpty: '',
            errors: {}
        }

        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
    }

    onChange(e) {
        this.setState({ [e.target.name]: e.target.value })
    }
    onSubmit(e) {
        e.preventDefault()
        if(!this.state.email){
            this.setState({
                emailEmpty: 'Please enter email',
                passwordEmpty: '',
            })
        }else if(!this.state.password){
            this.setState({
                emailEmpty: '',
                passwordEmpty: 'Please enter password',
            })
        }else{

            const user = {
                email: this.state.email,
                password: this.state.password
            }

            login(user).then(res => {
                if (res) {
                    alert('Login Successfull')
                    this.props.history.push(`/profile`)
                }
            })
        }

    }

    render() {
        return (
            <Container>
                <Row>
                    <Col lg={4} md={4} sm={12} className="mt-5 mx-auto">
                        <h1 className="h3 mb-3 font-weight-normal h1-custom">
                            Please sign in
                        </h1>
                        <br/>
                        <form noValidate onSubmit={this.onSubmit} className="form-css">
                            <div className="form-group">
                                <input
                                    type="email"
                                    required="required"
                                    className="form-control"
                                    name="email"
                                    placeholder="Enter Email"
                                    value={this.state.email}
                                    onChange={this.onChange}
                                />
                                <p className="error-text">{this.state.emailEmpty}</p>
                            </div>
                            <div className="form-group">
                                <input
                                    type="password"
                                    required="required"
                                    className="form-control"
                                    name="password"
                                    placeholder="Password"
                                    value={this.state.password}
                                    onChange={this.onChange}
                                />
                                <p className="error-text">{this.state.passwordEmpty}</p>
                            </div>
                            <button
                                type="submit"
                                className="btn btn-lg btn-primary btn-block"
                            >
                                Sign in
                            </button>
                        </form>
                    </Col>
                </Row>
            </Container>
        )
    }
}

export default Login
